import React, { useState, useEffect } from 'react';

function App() {
  const [gastos, setGastos] = useState([]);
  const [nome, setNome] = useState('');
  const [data, setData] = useState('');
  const [valor, setValor] = useState('');

  // Carregar gastos salvos do localStorage ao iniciar
  useEffect(() => {
    const gastosSalvos = JSON.parse(localStorage.getItem('gastos')) || [];
    setGastos(gastosSalvos);
  }, []);

  // Atualizar o localStorage sempre que a lista de gastos mudar
  useEffect(() => {
    localStorage.setItem('gastos', JSON.stringify(gastos));
  }, [gastos]);

  // Função para adicionar um gasto
  const adicionarGasto = () => {
    if (nome === '' || data === '' || valor === '') {
      alert('Por favor, preencha todos os campos!');
      return;
    }

    const novoGasto = {
      nome,
      data,
      valor: parseFloat(valor)
    };

    setGastos([...gastos, novoGasto]);

    // Limpar os campos após adicionar
    setNome('');
    setData('');
    setValor('');
  };

  // Função para remover um gasto pelo índice
  const removerGasto = (index) => {
    const novosGastos = gastos.filter((_, i) => i !== index);
    setGastos(novosGastos);
  };

  // Somar o total de todos os gastos
  const totalGastos = gastos.reduce((total, gasto) => total + gasto.valor, 0);

  return (
    <div>
      <h1>Registro de Gastos</h1>

      <input
        type="text"
        placeholder="Nome"
        value={nome}
        onChange={(e) => setNome(e.target.value)}
      />
      <input
        type="date"
        value={data}
        onChange={(e) => setData(e.target.value)}
      />
      <input
        type="number"
        placeholder="Valor"
        value={valor}
        onChange={(e) => setValor(e.target.value)}
      />

      <button onClick={adicionarGasto}>Adicionar Gasto</button>

      <ul>
        {gastos.map((gasto, index) => (
          <li key={index}>
            {gasto.nome} - {gasto.data} - R$ {gasto.valor.toFixed(2)}
            <button onClick={() => removerGasto(index)}>Remover</button>
          </li>
        ))}
      </ul>

      <h2>Total Gasto: R$ {totalGastos.toFixed(2)}</h2>
    </div>
  );
}

export default App;
