import React, { useState, useEffect } from 'react';
// Importações do Material UI (opcional)
import { Container, TextField, Button, List, ListItem, Typography } from '@mui/material';

function App() {
  const [gastos, setGastos] = useState([]);
  const [nome, setNome] = useState('');
  const [data, setData] = useState('');
  const [valor, setValor] = useState('');

  // Carregar os gastos armazenados localmente
  useEffect(() => {
    const gastosSalvos = JSON.parse(localStorage.getItem('gastos')) || [];
    setGastos(gastosSalvos);
  }, []);

  // Salvar os gastos no localStorage
  useEffect(() => {
    localStorage.setItem('gastos', JSON.stringify(gastos));
  }, [gastos]);

  // Função para adicionar um novo gasto
  const adicionarGasto = () => {
    if (nome && data && valor) {
      const novoGasto = { nome, data, valor: parseFloat(valor) };
      setGastos([...gastos, novoGasto]);
      setNome('');
      setData('');
      setValor('');
    }
  };

  // Função para remover um gasto
  const removerGasto = (index) => {
    const novosGastos = gastos.filter((_, i) => i !== index);
    setGastos(novosGastos);
  };

  // Calcular o valor total
  const totalGastos = gastos.reduce((total, gasto) => total + gasto.valor, 0);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Registro de Gastos</Typography>

      <TextField
        label="Nome"
        value={nome}
        onChange={(e) => setNome(e.target.value)}
        fullWidth
        margin="normal"
      />

      <TextField
        label="Data"
        type="date"
        value={data}
        onChange={(e) => setData(e.target.value)}
        fullWidth
        margin="normal"
        InputLabelProps={{ shrink: true }}
      />

      <TextField
        label="Valor"
        type="number"
        value={valor}
        onChange={(e) => setValor(e.target.value)}
        fullWidth
        margin="normal"
      />

      <Button variant="contained" color="primary" onClick={adicionarGasto} fullWidth>
        Adicionar Gasto
      </Button>

      <List>
        {gastos.map((gasto, index) => (
          <ListItem key={index}>
            <Typography variant="body1">
              {gasto.nome} - {gasto.data} - R$ {gasto.valor.toFixed(2)}
            </Typography>
            <Button color="secondary" onClick={() => removerGasto(index)}>Remover</Button>
          </ListItem>
        ))}
      </List>

      <Typography variant="h6" gutterBottom>Total Gasto: R$ {totalGastos.toFixed(2)}</Typography>
    </Container>
  );
}

export default App;
